INSERT INTO `multi_lang` VALUES(598, 299, 'Mass demonstrations broke out in Egypt after Friday prayers called Juma a al-Ghadab, on the fourth day of the demonstrations demanding the departure of President Hosni Mubarak.', 'EN');
