INSERT INTO `multi_lang` VALUES(608, 304, 'Egyptian President Mohamed Hosni Mubarak appoints the head of the General Intelligence Service, Major General Omar Suleiman as vice president, and assigns Ahmed Shafiq to form a government to replace former Prime Minister Ahmed Nazif, five days after the outbreak of the revolution of January 25.', 'EN');
