INSERT INTO `wp_0649z4bg2a_posts` VALUES(1, 1, '2019-03-25 07:48:00', '2019-03-25 05:48:00', 'What goes into a blog post? Helpful, industry-specific content that: 1) gives readers a useful takeaway, and 2) shows you''re an industry expert. \r\n\r\nUse your company''s blog posts to opine on current industry topics, humanize your company, and show how your products and services can help people.', 'Blog Post Title', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2019-03-25 07:48:00', '2019-03-25 05:48:00', '', 0, 'http://www.3otal.com/?p=1', 0, 'post', '', 1);
